library(ggplot2)
findClass<-function(df,c,label){
  i=length(c)
  a=c()
  for(i in c){
    a=c(a,df[which(df$SCN==i),11])
  }
  #print(a)
  qplot(a,main=label,xlab="Centroid")
  print(length(a[a==2]))
  print(length(a[a==4]))
}

